globalStyles:
@import url('https://fonts.googleapis.com/css2?family=Merriweather:wght@400;700;900&display=swap');
body {
    font-family: 'Merriweather', sans-serif;
    background-color: #ffffff;
    color: #000000;
    margin: 0;
    padding: 0;
    line-height: 1.6;
}

h1, h2, h3, h4, h5, h6 {
    font-family: 'Merriweather', sans-serif;
    font-weight: 700;
    margin: 0;
}

* {
    box-sizing: border-box;
}
